SELECT\n    features.featureTitle AS title, \n    features.featureSummary AS body, \n    features.postedOn AS dummy, \n    DATE_FORMAT(features.postedOn, '%M %d, %Y') AS posted, \n    NULL, \n    NULL, \n    staff.staffName, \n    features.featureID \nFROM \n    features \n    LEFT JOIN staff ON features.staffID = staff.staffID \nWHERE \n    features.postedOn != (SELECT MAX(postedOn) FROM features)\nORDER BY \n    dummy DESC \nLIMIT 0, 15;