SELECT a.spubid, a.title, s.status, p.name\nFROM articles a\nINNER JOIN status s ON a.spubid = s.spubid\nINNER JOIN people p ON a.spubid = p.spubid\nWHERE p.saffil = 'ABC' AND a.keywords LIKE '%keyword%'\nUNION ALL\nSELECT b.spubid, b.title, s.status, p.name\nFROM books b\nINNER JOIN status s ON b.spubid = s.spubid\nINNER JOIN people p ON b.spubid = p.spubid\nWHERE p.saffil = 'ABC' AND b.keywords LIKE '%keyword%'\nUNION ALL\nSELECT p.spubid, p.title, s.status, pe.name\nFROM papers p\nINNER JOIN status s ON p.spubid = s.spubid\nINNER JOIN people pe ON p.spubid = pe.spubid\nWHERE pe.saffil = 'ABC' AND p.keywords LIKE '%keyword%'\nORDER BY spubid;