-- Step 1: Create the temporary table without the serial column\nCREATE TEMP TABLE tempTable (folio INT);\n\n-- Step 2: Insert the ordered data into the temporary table\nINSERT INTO tempTable (folio)\nSELECT folio FROM regularTable ORDER BY folio;\n\n-- Step 3: Create another temporary table with a serial column for continuous numbering\nCREATE TEMP TABLE tempTableWithSerial (id SERIAL, folio INT);\n\n-- Step 4: Insert the data from the first temp table into the second temp table\nINSERT INTO tempTableWithSerial (folio)\nSELECT folio FROM tempTable;\n\n-- Now you can use tempTableWithSerial for pagination\nSELECT * FROM tempTableWithSerial WHERE id BETWEEN X AND Y;