DECLARE @JobID UNIQUEIDENTIFIER = CONVERT(UNIQUEIDENTIFIER, $(ESCAPE_NONE(JOBID)));\n   DECLARE @StepID INT = $(ESCAPE_NONE(STEPID));\n   DECLARE @StepName NVARCHAR(128) = 'Your Step Name';\n   \n   BEGIN TRY\n       -- Execute your stored procedure\n       EXEC YourStoredProcedure;\n\n       -- Log success\n       INSERT INTO JobStepLog (JobID, StepID, StepName, Status)\n       VALUES (@JobID, @StepID, @StepName, 'Success');\n   END TRY\n   BEGIN CATCH\n       -- Log failure\n       INSERT INTO JobStepLog (JobID, StepID, StepName, Status)\n       VALUES (@JobID, @StepID, @StepName, 'Fail');\n       \n       -- Re-throw the error to ensure the job step is marked as failed\n       THROW;\n   END CATCH;