SELECT\n    word,\n    GREATEST(ABS(big), ABS(expensive), ABS(smart), ABS(fast)) AS max_abs_value,\n    CASE\n        WHEN ABS(big) = GREATEST(ABS(big), ABS(expensive), ABS(smart), ABS(fast)) THEN 'big'\n        WHEN ABS(expensive) = GREATEST(ABS(big), ABS(expensive), ABS(smart), ABS(fast)) THEN 'expensive'\n        WHEN ABS(smart) = GREATEST(ABS(big), ABS(expensive), ABS(smart), ABS(fast)) THEN 'smart'\n        WHEN ABS(fast) = GREATEST(ABS(big), ABS(expensive), ABS(smart), ABS(fast)) THEN 'fast'\n    END AS attribute,\n    CASE\n        WHEN big = GREATEST(ABS(big), ABS(expensive), ABS(smart), ABS(fast)) THEN 'yes'\n        WHEN expensive = GREATEST(ABS(big), ABS(expensive), ABS(smart), ABS(fast)) THEN 'yes'\n        WHEN smart = GREATEST(ABS(big), ABS(expensive), ABS(smart), ABS(fast)) THEN 'yes'\n        WHEN fast = GREATEST(ABS(big), ABS(expensive), ABS(smart), ABS(fast)) THEN 'yes'\n        ELSE 'no'\n    END AS is_positive\nFROM\n    your_table_name;