USE [$(DatabaseName)]\nGO\n\nDECLARE @jobId BINARY(16)\nEXEC msdb.dbo.sp_add_job @job_name=N'YourJobName', \n        @enabled=1, \n        @notify_level_eventlog=0, \n        @notify_level_email=0, \n        @notify_level_netsend=0, \n        @notify_level_page=0, \n        @delete_level=0, \n        @description=N'Your job description', \n        @category_name=N'[Uncategorized (Local)]', \n        @owner_login_name=N'$(OwnerAccount)', \n        @job_id = @jobId OUTPUT\nGO\n\n-- Add a job step\nEXEC msdb.dbo.sp_add_jobstep @job_id=@jobId, @step_name=N'Step1', \n        @step_id=1, \n        @cmdexec_success_code=0, \n        @on_success_action=1, \n        @on_fail_action=2, \n        @retry_attempts=0, \n        @retry_interval=0, \n        @os_run_priority=0, @subsystem=N'TSQL', \n        @command=N'-- Your T-SQL command here', \n        @database_name=N'$(DatabaseName)', \n        @flags=0\nGO\n\n-- Schedule the job\nEXEC msdb.dbo.sp_add_jobschedule @job_id=@jobId, @name=N'Schedule1', \n        @enabled=1, \n        @freq_type=4, \n        @freq_interval=1, \n        @freq_subday_type=8, \n        @freq_subday_interval=5, \n        @freq_relative_interval=0, \n        @freq_recurrence_factor=0, \n        @active_start_date=20211001, \n        @active_start_time=233000, \n        @active_end_date=99991231, \n        @active_end_time=235959\nGO\n\n-- Add the job to the server\nEXEC msdb.dbo.sp_add_jobserver @job_id = @jobId, @server_name = N'$(ServerName)'\nGO