SELECT \n    j.name AS JobName,\n    r.start_execution_date,\n    DATEDIFF(minute, r.start_execution_date, GETDATE()) AS RunningDurationMinutes\nFROM \n    msdb.dbo.sysjobs j\nJOIN \n    msdb.dbo.sysjobactivity a ON j.job_id = a.job_id\nJOIN \n    msdb.dbo.sysjobhistory h ON a.job_id = h.job_id AND a.job_history_id = h.instance_id\nJOIN \n    msdb.dbo.sysjobactivity r ON j.job_id = r.job_id\nWHERE \n    r.stop_execution_date IS NULL\n    AND r.start_execution_date IS NOT NULL;