SELECT \n    j.name AS JobName,\n    s.next_run_date,\n    s.next_run_time\nFROM \n    msdb.dbo.sysjobs j\nJOIN \n    msdb.dbo.sysjobschedules js ON j.job_id = js.job_id\nJOIN \n    msdb.dbo.sysschedules s ON js.schedule_id = s.schedule_id\nWHERE \n    s.next_run_date > CONVERT(VARCHAR, GETDATE(), 112)\n    OR (s.next_run_date = CONVERT(VARCHAR, GETDATE(), 112) AND s.next_run_time > CONVERT(VARCHAR, GETDATE(), 108))\nORDER BY \n    s.next_run_date, s.next_run_time;