DATABASE mydatabase;\n\n-- Create a procedure to grant select on all tables\nCREATE PROCEDURE grant_select_all_tables()\nDEFINE tabname VARCHAR(128);\nDEFINE owner VARCHAR(128);\nDEFINE grant_stmt VARCHAR(255);\n\nFOREACH SELECT tabname, owner FROM systables WHERE tabid >= 100 AND owner != 'informix'\n    LET grant_stmt = 'GRANT SELECT ON "' || owner || '"."' || tabname || '" TO someuser';\n    EXECUTE IMMEDIATE grant_stmt;\nEND FOREACH;\n\nEND PROCEDURE;\n\n-- Call the procedure to apply the grants\nEXECUTE PROCEDURE grant_select_all_tables();