CREATE TABLE MarketBenchmarks (\n       BenchmarkID INT PRIMARY KEY,\n       BenchmarkName VARCHAR(50),\n       BenchmarkValue DECIMAL(18, 4),\n       Date DATE\n   );\n\n   CREATE TABLE PricingFormulas (\n       FormulaID INT PRIMARY KEY,\n       FormulaName VARCHAR(50),\n       FormulaExpression VARCHAR(255)\n   );\n\n   CREATE TABLE Deals (\n       DealID INT PRIMARY KEY,\n       BenchmarkID INT,\n       FormulaID INT,\n       DealDate DATE,\n       FOREIGN KEY (BenchmarkID) REFERENCES MarketBenchmarks(BenchmarkID),\n       FOREIGN KEY (FormulaID) REFERENCES PricingFormulas(FormulaID)\n   );