-- Step 1: Add the new 'id' column\nALTER TABLE your_table ADD id NUMBER;\n\n-- Step 2: Create the sequence\nCREATE SEQUENCE your_sequence START WITH 1 INCREMENT BY 1;\n\n-- Step 3: Update existing rows with unique IDs\nUPDATE your_table\nSET id = your_sequence.NEXTVAL;\n\n-- Step 4: Create a trigger to handle auto-increment for new rows\nCREATE OR REPLACE TRIGGER your_trigger\nBEFORE INSERT ON your_table\nFOR EACH ROW\nBEGIN\n  IF :NEW.id IS NULL THEN\n    SELECT your_sequence.NEXTVAL INTO :NEW.id FROM dual;\n  END IF;\nEND;\n/