DECLARE @SaltCount INT;\nSELECT @SaltCount = COUNT(*) FROM tmp_NewLogin;\nPRINT 'Set Salt values for all records' + CAST(@SaltCount AS VARCHAR(10));\n\nDECLARE @CharPool CHAR(83);\nSET @CharPool = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!"#$%&()*+,-./:;<=>?@';\n\n-- Function to generate a random string\nCREATE FUNCTION dbo.GenerateRandomString(@length INT)\nRETURNS VARCHAR(8000)\nAS\nBEGIN\n    DECLARE @result VARCHAR(8000) = '';\n    DECLARE @i INT = 0;\n    WHILE @i < @length\n    BEGIN\n        SET @result = @result + SUBSTRING(@CharPool, ABS(CHECKSUM(NEWID())) % 83 + 1, 1);\n        SET @i = @i + 1;\n    END\n    RETURN @result;\nEND;\n\n-- Update all records with a null Salt value\nUPDATE [table]\nSET [Salt] = dbo.GenerateRandomString(10)\nWHERE [Salt] IS NULL;\n\nPRINT 'Completed setting salts for all records';