SELECT Item.Id\n   FROM Item\n   JOIN Event ON Event.Id = Item.EventId\n   JOIN Seat USING (VenueId, Configuration)\n   WHERE Seat.Section = Item.Section\n   AND Event.Home_team = 1\n   AND Seat.exclude = 0\n   UNION\n   SELECT Item.Id\n   FROM Item\n   JOIN Event ON Event.Id = Item.EventId\n   JOIN Seat USING (VenueId, Configuration)\n   WHERE Seat.Section = Item.Section\n   AND Event.Home_team = 2\n   AND Seat.exclude = 0\n   UNION\n   SELECT Item.Id\n   FROM Item\n   JOIN Event ON Event.Id = Item.EventId\n   JOIN Seat USING (VenueId, Configuration)\n   WHERE Seat.Section = Item.Section\n   AND Event.Home_team = 3\n   AND Seat.exclude = 0\n   UNION\n   SELECT Item.Id\n   FROM Item\n   JOIN Event ON Event.Id = Item.EventId\n   JOIN Seat USING (VenueId, Configuration)\n   WHERE Seat.Section = Item.Section\n   AND Event.Home_team = 4\n   AND Seat.exclude = 0\n   UNION\n   SELECT Item.Id\n   FROM Item\n   JOIN Event ON Event.Id = Item.EventId\n   JOIN Seat USING (VenueId, Configuration)\n   WHERE Seat.Section = Item.Section\n   AND Event.Home_team = 5\n   AND Seat.exclude = 0;