/*\n * Master manufacturers shared between all customers\n */\nCREATE TABLE master_manufacturers (\n    master_manufacturer_id INTEGER NOT NULL PRIMARY KEY,\n    name VARCHAR(100) NOT NULL,\n    attribute_1 VARCHAR(50),\n    /* ... */\n    attribute_n VARCHAR(50)\n);\n\nINSERT INTO master_manufacturers (master_manufacturer_id, name)\nVALUES\n    (1, 'Ford'),\n    (2, 'Chevrolet'),\n    (3, 'BMW');\n\n/*\n * Customer-specific manufacturers. If master_manufacturer_id IS NULL,\n * then it is a custom manufacturer.\n */\nCREATE TABLE customer_manufacturers (\n    customer_manufacturer_id INTEGER NOT NULL PRIMARY KEY,\n    customer_id INTEGER NOT NULL,\n    master_manufacturer_id INTEGER,\n    name VARCHAR(100) NOT NULL,\n    attribute_1 VARCHAR(50),\n    /* ... */\n    attribute_n VARCHAR(50),\n    FOREIGN KEY (master_manufacturer_id) REFERENCES master_manufacturers(master_manufacturer_id)\n);