/*\n * Master models shared between all customers\n */\nCREATE TABLE master_models (\n    master_model_id INTEGER NOT NULL PRIMARY KEY,\n    master_manufacturer_id INTEGER NOT NULL,\n    name VARCHAR(100) NOT NULL,\n    attribute_1 VARCHAR(50),\n    /* ... */\n    attribute_n VARCHAR(50),\n    FOREIGN KEY (master_manufacturer_id) REFERENCES master_manufacturers(master_manufacturer_id)\n);\n\nINSERT INTO master_models (master_model_id, master_manufacturer_id, name)\nVALUES\n    (1, 1, 'F-150'),\n    (2, 2, 'Camaro'),\n    (3, 3, 'M3');\n\n/*\n * Customer-specific models. If master_model_id IS NULL, then it is a custom model.\n */\nCREATE TABLE customer_models (\n    customer_model_id INTEGER NOT NULL PRIMARY KEY,\n    customer_id INTEGER NOT NULL,\n    master_model_id INTEGER,\n    customer_manufacturer_id INTEGER,\n    name VARCHAR(100) NOT NULL,\n    attribute_1 VARCHAR(50),\n    /* ... */\n    attribute_n VARCHAR(50),\n    FOREIGN KEY (master_model_id) REFERENCES master_models(master_model_id),\n    FOREIGN KEY (customer_manufacturer_id) REFERENCES customer_manufacturers(customer_manufacturer_id)\n);