UPDATE lessonstatus\nINNER JOIN user ON lessonstatus.user_id = user.user_id\nINNER JOIN (\n    SELECT lessonstatus.lesson_id\n    FROM lessonstatus\n    WHERE user_id = 1\n) AS filtered_lessons ON lessonstatus.lesson_id != filtered_lessons.lesson_id\nSET lessonstatus.user_id = (\n    SELECT user_id \n    FROM user \n    WHERE username = 'too_many_accounts'\n)\nWHERE user.last_name = 'stupid' \nAND user.first_name = 'user'\nAND user.username != 'too_many_accounts';