SELECT p.ProfileID, p.UserID, p.ProfileLanguage, p.ProfilePriority\nFROM Profile p\nJOIN (\n    SELECT UserID, MIN(ProfilePriority) AS ProfilePriority\n    FROM Profile\n    WHERE ProfileLanguage = 'es-MX' OR ProfilePriority = 2\n    GROUP BY UserID\n) sub\nON p.UserID = sub.UserID AND p.ProfilePriority = sub.ProfilePriority\nWHERE p.ProfileLanguage = 'es-MX' OR p.ProfilePriority = 2