-- Step 1: Create the Summary Table\nCREATE TABLE FormSummary (\n    FormID INT PRIMARY KEY,\n    SummaryText NVARCHAR(MAX)\n);\n\n-- Step 2: Create Full-Text Index on the Summary Table\nCREATE FULLTEXT CATALOG FormSummaryCatalog;\nCREATE FULLTEXT INDEX ON FormSummary(SummaryText) \n    KEY INDEX PK_FormSummary \n    ON FormSummaryCatalog;\n\n-- Step 3: Create Triggers to Populate/Update the Summary Table\nCREATE TRIGGER trg_InsertAnswer ON Answer\nAFTER INSERT\nAS\nBEGIN\n    UPDATE FormSummary\n    SET SummaryText = SummaryText + ' ' + inserted.AnswerText\n    FROM FormSummary\n    INNER JOIN inserted ON FormSummary.FormID = inserted.FormID;\nEND;\n\nCREATE TRIGGER trg_InsertComment ON Comment\nAFTER INSERT\nAS\nBEGIN\n    UPDATE FormSummary\n    SET SummaryText = SummaryText + ' ' + inserted.CommentText\n    FROM FormSummary\n    INNER JOIN inserted ON FormSummary.FormID = inserted.FormID;\nEND;\n\n-- You may need similar triggers for UPDATE and DELETE operations on Answer and Comment tables\n\n-- Step 4: Perform Full-Text Search on the Summary Table\nDECLARE @SearchTerm NVARCHAR(100) = 'your search term';\nSELECT FormID\nFROM FormSummary\nWHERE CONTAINS(SummaryText, @SearchTerm);