SELECT a2.AssetId\nFROM Attributes a1\nJOIN Attributes a2 ON a1.AttributeId = a2.AttributeId\nWHERE a1.AssetId = (SELECT AssetId FROM Assets WHERE AssetName = 'Asset-A')\n  AND a2.AssetId != (SELECT AssetId FROM Assets WHERE AssetName = 'Asset-A')\nGROUP BY a2.AssetId\nHAVING COUNT(DISTINCT a2.AttributeId) = (SELECT COUNT(DISTINCT AttributeId) FROM Attributes WHERE AssetId = (SELECT AssetId FROM Assets WHERE AssetName = 'Asset-A'));