-- Assuming you have a table named 'YourTable' with a primary key column 'ID'\n\nDECLARE @PageNumber INT = 1  -- Change this to the desired page number\nDECLARE @PageSize INT = 100  -- Change this to the desired page size\n\nWITH PaginatedRecords AS\n(\n    SELECT \n        ROW_NUMBER() OVER (ORDER BY ID) AS RowNum,\n        *\n    FROM \n        YourTable\n)\nSELECT \n    *\nFROM \n    PaginatedRecords\nWHERE \n    RowNum BETWEEN (@PageNumber - 1) * @PageSize + 1 AND @PageNumber * @PageSize\nORDER BY \n    RowNum;