SELECT pm_info.is_read, \n       sender.usrFirst as sender_name, \n       pm_data.date_sent,  \n       pm_data.title, \n       pm_data.thread_id, \n       pm_data.id as dataid, \n       thread_max_date_sent\nFROM pm_info\nINNER JOIN pm_data ON pm_info.message_id = pm_data.id\nINNER JOIN tblUsers AS sender ON pm_data.sender_id = sender.usrID\nINNER JOIN (\n    SELECT thread_id, MAX(date_sent) AS thread_max_date_sent \n    FROM pm_data \n    GROUP BY thread_id\n) deriv1 ON pm_data.thread_id = deriv1.thread_id \n        AND pm_data.date_sent = deriv1.thread_max_date_sent\nWHERE pm_info.receiver_id = '$usrID'\nORDER BY deriv1.thread_max_date_sent DESC;