SELECT n.note_id, n.note_content\nFROM notes n\nJOIN note_labels nl1 ON n.note_id = nl1.note_id\nJOIN labels l1 ON nl1.label_id = l1.label_id\nJOIN note_labels nl2 ON n.note_id = nl2.note_id\nJOIN labels l2 ON nl2.label_id = l2.label_id\nJOIN note_labels nl3 ON n.note_id = nl3.note_id\nJOIN labels l3 ON nl3.label_id = l3.label_id\nWHERE l1.label_name = 'one'\nAND l2.label_name = 'two'\nAND l3.label_name = 'three';