-- Define a partition function\nCREATE PARTITION FUNCTION logdate_range (datetime)\nAS RANGE LEFT FOR VALUES ('2021-01-01', '2022-01-01', '2023-01-01');\n\n-- Define a partition scheme\nCREATE PARTITION SCHEME logdate_scheme\nAS PARTITION logdate_range\nTO (filegroup1, filegroup2, filegroup3, filegroup4);\n\n-- Create a partitioned table\nCREATE TABLE logs (\n    id INT PRIMARY KEY,\n    logdate DATETIME,\n    error_message NVARCHAR(255),\n    url NVARCHAR(255)\n) ON logdate_scheme(logdate);