-- Replace ForeignKeyTable and PrimaryKeyTable with your actual table names\n-- Replace ForeignKeyColumn and PrimaryKeyColumn with your actual column names\n\nSELECT fk_table.name AS ForeignKeyTable,\n       fk_col.name AS ForeignKeyColumn,\n       pk_table.name AS PrimaryKeyTable,\n       pk_col.name AS PrimaryKeyColumn\nFROM sys.foreign_key_columns fk\nJOIN sys.columns fk_col\n    ON fk_col.column_id = fk.parent_column_id\n    AND fk_col.object_id = fk.parent_object_id\nJOIN sys.tables fk_table\n    ON fk_table.object_id = fk.parent_object_id\nJOIN sys.columns pk_col\n    ON pk_col.column_id = fk.referenced_column_id\n    AND pk_col.object_id = fk.referenced_object_id\nJOIN sys.tables pk_table\n    ON pk_table.object_id = fk.referenced_object_id\nWHERE fk_table.name = 'ForeignKeyTable'\nAND pk_table.name = 'PrimaryKeyTable';