-- Step 1: Create temporary tables and insert data\n\n-- Temporary table for COSTCENT\nCREATE TABLE #TEMPCC (\n    COST_CTR_NUM INT,\n    WBLANK CHAR(1)\n);\n\nINSERT INTO #TEMPCC (COST_CTR_NUM, WBLANK)\nSELECT CC_COSTCENT, ' ' FROM COSTCENT;\n\n-- Temporary table for JOINGLAC\nCREATE TABLE #TEMPAC (\n    ACCOUNT_NO INT,\n    LI_LNTM INT,\n    WBLANK CHAR(1)\n);\n\nINSERT INTO #TEMPAC (ACCOUNT_NO, LI_LNTM, WBLANK)\nSELECT ACCOUNT_NO, LI_LNTM, ' ' FROM JOINGLAC;\n\n-- Step 2: Perform the cross join and other operations\n\n-- Result temporary table\nCREATE TABLE #TEMPCCAC (\n    CA_JCCAC CHAR(16),\n    COST_CTR_NUM INT,\n    ACCOUNT_NO INT,\n    LI_LNTM INT\n);\n\n-- Insert results of cross join into result table\nINSERT INTO #TEMPCCAC (CA_JCCAC, COST_CTR_NUM, ACCOUNT_NO, LI_LNTM)\nSELECT \n    CAST(EDIT(T1.COST_CTR_NUM) + EDIT(T2.ACCOUNT_NO) AS CHAR(16)) AS CA_JCCAC,\n    T1.COST_CTR_NUM,\n    T2.ACCOUNT_NO,\n    T2.LI_LNTM\nFROM #TEMPCC T1\nCROSS JOIN #TEMPAC T2;\n\n-- Optional: Perform further operations like grouping or deduping here\n\n-- Step 3: Cleanup temporary tables\nDROP TABLE #TEMPCC;\nDROP TABLE #TEMPAC;\nDROP TABLE #TEMPCCAC;