-- Define a table type\nCREATE TYPE dbo.MyTableType AS TABLE\n(\n    ID INT,\n    Value NVARCHAR(50)\n);\nGO\n\n-- Procedure C: Processes data and returns a table result\nCREATE PROCEDURE dbo.ProcC\n    @InputTable dbo.MyTableType READONLY,\n    @OutputTable dbo.MyTableType OUTPUT\nAS\nBEGIN\n    INSERT INTO @OutputTable (ID, Value)\n    SELECT ID, UPPER(Value)\n    FROM @InputTable;\nEND;\nGO\n\n-- Procedure B: Calls ProcC and processes its results\nCREATE PROCEDURE dbo.ProcB\n    @InputTable dbo.MyTableType READONLY,\n    @OutputTable dbo.MyTableType OUTPUT\nAS\nBEGIN\n    DECLARE @TempTable dbo.MyTableType;\n\n    EXEC dbo.ProcC @InputTable, @TempTable OUTPUT;\n\n    INSERT INTO @OutputTable (ID, Value)\n    SELECT ID, LOWER(Value)\n    FROM @TempTable;\nEND;\nGO\n\n-- Procedure A: Calls ProcB and processes its results\nCREATE PROCEDURE dbo.ProcA\n    @InputTable dbo.MyTableType READONLY\nAS\nBEGIN\n    DECLARE @TempTable dbo.MyTableType;\n    DECLARE @FinalResult dbo.MyTableType;\n\n    EXEC dbo.ProcB @InputTable, @TempTable OUTPUT;\n\n    SELECT *\n    INTO #FinalResult\n    FROM @TempTable;\n\n    -- Further processing on #FinalResult\n\n    SELECT * FROM #FinalResult;\nEND;\nGO