-- Get all rulings for a specific card Id, including those that reference the card\nDECLARE @CardName NVARCHAR(255);\nDECLARE @CardId INT = 1; -- Replace with the desired card Id\n\nSELECT @CardName = Name FROM card WHERE Id = @CardId;\n\n-- Direct rulings for the card\nSELECT * FROM rulings WHERE Id = @CardId\nUNION\n-- Rulings where the card name is referenced in the text\nSELECT r.* FROM rulings r\nJOIN card c ON r.text LIKE '%' + QUOTENAME(@CardName, '"') + '%';