-- Example of creating a new table for a repeating group\nCREATE TABLE new_table (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    column1 VARCHAR(255),\n    column2 VARCHAR(255)\n);\n\n-- Add foreign key references in the original table\nALTER TABLE your_huge_table ADD COLUMN new_table_id INT;\nALTER TABLE your_huge_table ADD CONSTRAINT fk_new_table FOREIGN KEY (new_table_id) REFERENCES new_table(id);