BEGIN TRANSACTION;\n\nDECLARE @TrackStringId BIGINT;\n\n-- Insert into TrackStrings and get the new Id\nINSERT INTO [dbo].[TrackStrings] ([String])\nVALUES (N'YourTrackStringValue');\n\nSET @TrackStringId = SCOPE_IDENTITY();\n\n-- Insert into Tracks using the new TrackStringId\nINSERT INTO [dbo].[Tracks] ([TrackStringId], [Time])\nVALUES (@TrackStringId, GETDATE());\n\nCOMMIT TRANSACTION;