DECLARE @searchString NVARCHAR(100);\nSET @searchString = 'your_search_string';\n\n-- Declare a variable to store the parsed integer value if @searchString can be converted to an INT\nDECLARE @parsedInt INT;\nSET @parsedInt = NULL;\n\n-- Try to convert @searchString to an INT\nBEGIN TRY\n    SET @parsedInt = CAST(@searchString AS INT);\nEND TRY\nBEGIN CATCH\n    SET @parsedInt = NULL;\nEND CATCH;\n\n-- Combine the exact match with the full-text search results\nSELECT *\nFROM\n(\n    -- First part: exact match\n    SELECT \n        t.*,\n        -1 AS Rank,  -- Assign a rank of -1 to give it the highest priority\n        1 AS SortOrder\n    FROM \n        YourTable t\n    WHERE \n        t.PK = @parsedInt\n    \n    UNION ALL\n    \n    -- Second part: full-text search results\n    SELECT \n        t.*,\n        ft.Rank,\n        2 AS SortOrder\n    FROM \n        YourTable t\n    INNER JOIN \n        FREETEXTTABLE(YourTable, YourColumn, @searchString) ft\n    ON \n        t.PK = ft.[KEY]\n) AS CombinedResults\nORDER BY\n    SortOrder,  -- This ensures that exact matches come first\n    Rank DESC   -- This orders the full-text search results by rank