-- Get the row with the MAX TreatAuthDate\nSELECT tblSignature.*\nFROM tblSignature\nJOIN (\n    SELECT MAX(TreatAuthDate) AS MaxTreatAuthDate, PatientID\n    FROM tblSignature\n    WHERE PatientID = 12345\n    GROUP BY PatientID\n) AS MaxTreatAuthDateResult\nON tblSignature.TreatAuthDate = MaxTreatAuthDateResult.MaxTreatAuthDate\nAND tblSignature.PatientID = MaxTreatAuthDateResult.PatientID\nWHERE tblSignature.PatientID = 12345;\n\n-- Get the row with the MAX Hipaadate\nSELECT tblSignature.*\nFROM tblSignature\nJOIN (\n    SELECT MAX(Hipaadate) AS MaxHipaadate, PatientID\n    FROM tblSignature\n    WHERE PatientID = 12345\n    GROUP BY PatientID\n) AS MaxHipaadateResult\nON tblSignature.Hipaadate = MaxHipaadateResult.MaxHipaadate\nAND tblSignature.PatientID = MaxHipaadateResult.PatientID\nWHERE tblSignature.PatientID = 12345;\n\n-- Get the row with the MAX DrugTestDate\nSELECT tblSignature.*\nFROM tblSignature\nJOIN (\n    SELECT MAX(DrugTestDate) AS MaxDrugTestDate, PatientID\n    FROM tblSignature\n    WHERE PatientID = 12345\n    GROUP BY PatientID\n) AS MaxDrugTestDateResult\nON tblSignature.DrugTestDate = MaxDrugTestDateResult.MaxDrugTestDate\nAND tblSignature.PatientID = MaxDrugTestDateResult.PatientID\nWHERE tblSignature.PatientID = 12345;