BEGIN TRANSACTION;\n\nBEGIN TRY\n    -- Delete from the relationship table first\n    DELETE FROM tGeoQuizToQuestion\n    WHERE quizId = @quizId;\n\n    -- Delete from the question table\n    DELETE FROM tGeoQuestions\n    WHERE id IN (SELECT questionid FROM tGeoQuizToQuestion WHERE quizId = @quizId);\n\n    COMMIT TRANSACTION;\nEND TRY\nBEGIN CATCH\n    ROLLBACK TRANSACTION;\n    -- Handle the error\n    THROW;\nEND CATCH;