-- Create a stored procedure to start the SQL Agent job\nCREATE PROCEDURE StartJobProcedure\nAS\nBEGIN\n    DECLARE @job_name NVARCHAR(128) = 'YourJobName';\n    EXEC msdb.dbo.sp_start_job @job_name = @job_name;\nEND;\n\n-- Alter queue to use the activation procedure\nALTER QUEUE NotifyQueue\nWITH ACTIVATION \n(\n    STATUS = ON,\n    PROCEDURE_NAME = StartJobProcedure,\n    MAX_QUEUE_READERS = 1,\n    EXECUTE AS SELF\n);