CREATE TRIGGER trg_TableNotEmpty\nON YourTable\nAFTER INSERT\nAS\nBEGIN\n    IF EXISTS (SELECT 1 FROM YourTable)\n    BEGIN\n        -- Send a message to the Service Broker queue\n        DECLARE @message NVARCHAR(MAX) = 'Table is non-empty';\n        DECLARE @dialog_handle UNIQUEIDENTIFIER;\n\n        BEGIN DIALOG CONVERSATION @dialog_handle\n        FROM SERVICE NotifyService\n        TO SERVICE 'NotifyService'\n        ON CONTRACT NotifyContract\n        WITH ENCRYPTION = OFF;\n\n        SEND ON CONVERSATION @dialog_handle\n        MESSAGE TYPE NotifyMessage (@message);\n    END\nEND;