-- Enable Service Broker\nALTER DATABASE YourDatabase SET ENABLE_BROKER;\n\n-- Create a message type\nCREATE MESSAGE TYPE NotifyMessage VALIDATION = NONE;\n\n-- Create a contract\nCREATE CONTRACT NotifyContract (NotifyMessage SENT BY INITIATOR);\n\n-- Create a queue\nCREATE QUEUE NotifyQueue;\n\n-- Create a service\nCREATE SERVICE NotifyService ON QUEUE NotifyQueue (NotifyContract);