WITH CTE_ProjectStatistics AS (\n    SELECT \n        ProjectID,\n        COUNT(TaskID) AS NumberOfTasks,\n        COUNT(MilestoneID) AS NumberOfMilestones,\n        -- Add other aggregated columns as needed\n    FROM \n        Projects P\n    LEFT JOIN \n        Tasks T ON P.ProjectID = T.ProjectID\n    LEFT JOIN \n        Milestones M ON P.ProjectID = M.ProjectID\n    GROUP BY \n        ProjectID\n)\nSELECT \n    ProjectID,\n    NumberOfTasks,\n    NumberOfMilestones\n    -- Add other columns as needed\nFROM \n    CTE_ProjectStatistics;