-- Create the look-up table for civil status\nCREATE TABLE CivilStatus (\n    StatusID INT PRIMARY KEY,\n    StatusName VARCHAR(50) NOT NULL\n);\n\n-- Insert the fixed values into the look-up table\nINSERT INTO CivilStatus (StatusID, StatusName) VALUES\n(1, 'Single'),\n(2, 'Married'),\n(3, 'Separated'),\n(4, 'Widow/Widower');\n\n-- Example of a table that references the look-up table\nCREATE TABLE Person (\n    PersonID INT PRIMARY KEY,\n    Name VARCHAR(100) NOT NULL,\n    CivilStatusID INT,\n    FOREIGN KEY (CivilStatusID) REFERENCES CivilStatus(StatusID)\n);