-- Table to store persons\nCREATE TABLE PERSON (\n    name VARCHAR(255) PRIMARY KEY\n);\n\n-- Intersection table to store marriages\nCREATE TABLE MARRIAGE (\n    person1_name VARCHAR(255),\n    person2_name VARCHAR(255),\n    PRIMARY KEY (person1_name, person2_name),\n    FOREIGN KEY (person1_name) REFERENCES PERSON(name),\n    FOREIGN KEY (person2_name) REFERENCES PERSON(name)\n);\n\n-- Table to ensure each person has only one spouse\nCREATE TABLE SPOUSE (\n    person_name VARCHAR(255) PRIMARY KEY,\n    spouse_name VARCHAR(255) UNIQUE,\n    FOREIGN KEY (person_name) REFERENCES PERSON(name),\n    FOREIGN KEY (spouse_name) REFERENCES PERSON(name)\n);