CREATE OR REPLACE PROCEDURE get_employee_count(\n    p_dept_id IN NUMBER,\n    p_result OUT NUMBER\n) AS\nBEGIN\n    SELECT COUNT(*)\n    INTO p_result\n    FROM employees\n    WHERE department_id = p_dept_id;\nEND;\n/\n\n-- Calling the stored procedure\nDECLARE\n    v_result NUMBER;\nBEGIN\n    get_employee_count(10, v_result);\n    DBMS_OUTPUT.PUT_LINE('Result: ' || v_result);\nEND;