MERGE INTO YourTable AS target\nUSING (SELECT CAST(GETDATE() AS DATE) AS TodayDate, @ScorerID AS ScorerID) AS source\nON target.ScorerID = source.ScorerID AND CAST(target.DateCreated AS DATE) = source.TodayDate\nWHEN MATCHED THEN\n    UPDATE SET target.Score = @Score\nWHEN NOT MATCHED THEN\n    INSERT (ScorerID, Score, DateCreated)\n    VALUES (source.ScorerID, @Score, GETDATE());