-- Utility function to handle NULL values\nCREATE OR REPLACE FUNCTION nvl_default(p_value IN NUMBER, p_default IN NUMBER)\nRETURN NUMBER\nIS\nBEGIN\n  RETURN NVL(p_value, p_default);\nEND;\n/\n\n-- Example function using the utility function\nCREATE OR REPLACE FUNCTION testFunction(\n  varNumber IN NUMBER DEFAULT 0\n)\nRETURN NUMBER\nAS\nBEGIN\n  -- Use the utility function to assign default value if NULL\n  varNumber := nvl_default(varNumber, 0);\n  \n  dbms_output.put_line(varNumber);\n  RETURN varNumber;\nEND;\n/