CREATE MATERIALIZED VIEW transaction_bins AS\nSELECT\n    CASE\n        WHEN transaction_value BETWEEN 0 AND 50 THEN '0-50'\n        WHEN transaction_value BETWEEN 50 AND 100 THEN '50-100'\n        WHEN transaction_value BETWEEN 100 AND 200 THEN '100-200'\n        ELSE '>200'\n    END AS value_range,\n    COUNT(*) AS transaction_count,\n    SUM(transaction_value) AS total_value\nFROM\n    transactions\nGROUP BY\n    value_range;