DELIMITER $$\n\nCREATE PROCEDURE SelectWithPrefixes()\nBEGIN\n    DECLARE column_list_a TEXT;\n    DECLARE column_list_b TEXT;\n    \n    -- Get column names for table A\n    SELECT GROUP_CONCAT(COLUMN_NAME) INTO column_list_a\n    FROM INFORMATION_SCHEMA.COLUMNS \n    WHERE TABLE_NAME = 'TABLE_A';\n    \n    -- Get column names for table B prefixed with 'b.'\n    SELECT GROUP_CONCAT(CONCAT('b.', COLUMN_NAME, ' AS `b.', COLUMN_NAME, '`')) INTO column_list_b\n    FROM INFORMATION_SCHEMA.COLUMNS \n    WHERE TABLE_NAME = 'TABLE_B';\n    \n    -- Build and execute the dynamic SQL query\n    SET @sql = CONCAT(\n        'SELECT ', column_list_a, ', ', column_list_b, \n        ' FROM TABLE_A a JOIN TABLE_B b USING (some_id);'\n    );\n\n    PREPARE stmt FROM @sql;\n    EXECUTE stmt;\n    DEALLOCATE PREPARE stmt;\nEND$$\n\nDELIMITER ;