WITH FixedPeriods AS (\n    SELECT 1 AS Period\n    UNION ALL\n    SELECT 2\n    UNION ALL\n    SELECT 3\n    UNION ALL\n    SELECT 4\n    UNION ALL\n    SELECT 5\n    UNION ALL\n    SELECT 6\n    UNION ALL\n    SELECT 7\n    UNION ALL\n    SELECT 8\n    UNION ALL\n    SELECT 9\n    UNION ALL\n    SELECT 10\n    UNION ALL\n    SELECT 11\n    UNION ALL\n    SELECT 12\n    UNION ALL\n    SELECT 13\n    UNION ALL\n    SELECT 14\n    UNION ALL\n    SELECT 15\n    UNION ALL\n    SELECT 16\n    UNION ALL\n    SELECT 17\n    UNION ALL\n    SELECT 18\n    UNION ALL\n    SELECT 19\n    UNION ALL\n    SELECT 20\n    UNION ALL\n    SELECT 21\n    UNION ALL\n    SELECT 22\n    UNION ALL\n    SELECT 23\n    UNION ALL\n    SELECT 24\n)\nSELECT fp.Period, COALESCE(p.Profit, 0) AS Profit\nFROM FixedPeriods fp\nLEFT JOIN Projections p ON fp.Period = p.Period\nORDER BY fp.Period;