CREATE TABLE vehicles (\n    vid INT PRIMARY KEY,\n    vin VARCHAR(50)\n);\n\nCREATE TABLE categories (\n    cid INT PRIMARY KEY,\n    category VARCHAR(50)\n);\n\nCREATE TABLE tags (\n    tid INT PRIMARY KEY,\n    cid INT,\n    tag VARCHAR(50),\n    FOREIGN KEY (cid) REFERENCES categories(cid)\n);\n\nCREATE TABLE vehicleTags (\n    vid INT,\n    cid INT,\n    tid INT,\n    PRIMARY KEY (vid, cid),\n    FOREIGN KEY (vid) REFERENCES vehicles(vid),\n    FOREIGN KEY (cid) REFERENCES categories(cid),\n    FOREIGN KEY (tid) REFERENCES tags(tid)\n);