-- Replace 'YourSessionID' with the actual session ID you're interested in\nDECLARE @SessionID NVARCHAR(88) = 'YourSessionID';\n\nSELECT \n    [SessionID],\n    [Created],\n    [Expires],\n    CAST([SessionItemShort] AS VARCHAR(MAX)) AS [SessionItemShort],\n    CAST([SessionItemLong] AS VARCHAR(MAX)) AS [SessionItemLong]\nFROM \n    [dbo].[ASPStateTempSessions]\nWHERE \n    [SessionID] = @SessionID;