SELECT\n    s.sid,\n    s.serial#,\n    s.username,\n    s.program,\n    s.osuser,\n    su.tablespace,\n    su.segtype,\n    su.contents,\n    su.blocks,\n    su.blocks * t.block_size / 1024 / 1024 AS mb_used,\n    sq.sql_text\nFROM\n    v$sort_usage su\nJOIN\n    v$session s ON su.session_addr = s.saddr\nJOIN\n    dba_tablespaces t ON su.tablespace = t.tablespace_name\nJOIN\n    v$sql sq ON s.sql_address = sq.address\nORDER BY\n    su.blocks DESC;