BEGIN TRANSACTION;\n\n-- Insert into table A\nINSERT INTO table_a (column1, column2) VALUES ('value1', 'value2');\n\n-- Get the last inserted ID\nDECLARE @last_id INT = SCOPE_IDENTITY();\n\n-- Use the generated ID to insert into table B\nINSERT INTO table_b (table_a_id, column3) VALUES (@last_id, 'value3');\n\nCOMMIT;