-- Step 1: Create the tables with deferrable foreign key constraints\nCREATE TABLE question (\n    id SERIAL PRIMARY KEY,\n    text TEXT NOT NULL,\n    correct_answer INTEGER,\n    CONSTRAINT fk_correct_answer FOREIGN KEY (correct_answer) REFERENCES answer(id) DEFERRABLE INITIALLY DEFERRED\n);\n\nCREATE TABLE answer (\n    id SERIAL PRIMARY KEY,\n    question_id INTEGER NOT NULL,\n    text TEXT NOT NULL,\n    CONSTRAINT fk_question FOREIGN KEY (question_id) REFERENCES question(id) DEFERRABLE INITIALLY DEFERRED\n);\n\n-- Step 2: Begin a transaction\nBEGIN;\n\n-- Step 3: Insert initial records with placeholders\nINSERT INTO question (text) VALUES ('What is the capital of France?');\nINSERT INTO answer (question_id, text) VALUES (1, 'Paris'), (1, 'London'), (1, 'Berlin');\n\n-- Step 4: Update the question with the correct answer reference\nUPDATE question SET correct_answer = (SELECT id FROM answer WHERE question_id = question.id AND text = 'Paris') WHERE id = 1;\n\n-- Step 5: Commit the transaction\nCOMMIT;