CREATE TABLE Post (\n    id AUTOINCREMENT PRIMARY KEY,\n    title TEXT,\n    text TEXT,\n    author INTEGER REFERENCES Person(id)\n);\n\nCREATE TABLE Person (\n    id AUTOINCREMENT PRIMARY KEY,\n    name TEXT\n);\n\nCREATE TABLE Photo (\n    id AUTOINCREMENT PRIMARY KEY,\n    title TEXT,\n    owner INTEGER REFERENCES Person(id),\n    path TEXT\n);\n\nCREATE TABLE Comments (\n    id AUTOINCREMENT PRIMARY KEY,\n    commentable_type TEXT,\n    commentable_id INTEGER,\n    text TEXT,\n    author INTEGER REFERENCES Person(id)\n);